INSERT INTO `comment_info` VALUES ('3', '53', '<泰国曼谷-芭提雅机票+当地5晚6或7日游>近万人游/美攻铁道市场/拉差达火车夜市/沙美岛出海/狂欢泼水/纯玩0自费/全程网评5星保证1晚万豪或喜来登', '行程少，导态度怠慢！', '好', '7', '0', '2020-04-19');
