-- ----------------------------
-- Table structure for `itinerary_route_info`
-- ----------------------------
DROP TABLE IF EXISTS `itinerary_route_info`;
CREATE TABLE `itinerary_route_info` (
  `id` int(100) NOT NULL AUTO_INCREMENT,
  `scenicSpotid` varchar(100) COLLATE utf8_unicode_ci DEFAULT NULL,
  `days` varchar(20) COLLATE utf8_unicode_ci DEFAULT NULL,
  `title` varchar(200) COLLATE utf8_unicode_ci DEFAULT NULL,
  `introduce` varchar(2000) COLLATE utf8_unicode_ci DEFAULT NULL,
  `photo` varchar(1000) COLLATE utf8_unicode_ci DEFAULT '',
  `photo2` varchar(1000) COLLATE utf8_unicode_ci DEFAULT '',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=95 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

-- ----------------------------
-- Records of itinerary_route_info
-- ----------------------------
INSERT INTO `itinerary_route_info` VALUES ('1', '1', '1', '入住酒店自由活动', '青石铺就的长巷，飘散着古城淡淡烟火，偶有行人悠闲走过，把恍惚的记忆，遗落在时光里，这就是闻着风都可以做梦的丽江古城。\r\n\r\n\r\n当您踏入丽江，接机师傅会在机场等您（接机师傅会在您出发前一天九点前跟您联系），接您去酒店，到达酒店稍作休息之后您就可以根据您的时间自由安排，您可以顺着丽江古城的青石板路一路闲逛，在青石板路上漫步，古香古色的丽江，道路两边轻轻流淌的河水，暖洋洋的阳光，会让您感觉时光慢一点，再慢一点，丽江的柔软时光和美丽的丽江欢迎您的到来❤\r\n您可以放心的自行安排时间，导游会在晚上21:00以前跟您联系，告知您第二天的行程安排和出发时间。\r\n\r\n\r\n\r\n水流双车转，桥小百座横，故道茶马远，古柳又新春。丽江古城有几百座桥，石拱桥、石板桥、木板桥或粗犷或婉转铺设在淙淙的流水之上，千姿百态，岸边垂柳依依，殷红的三角梅、鹅黄的千叶菊恣肆地怒放，仿佛素净点染的中国画泼进了浓墨重彩,分外妖娆。\r\n无论您是选择以怎样的方式度过和丽江初次邂逅，愿丽江的恬静和安然能冲淡您旅程的奔波和劳累，愿您一夜无梦，欣然安睡❤。', 'gmly.jpg', 'gmly2.jpg');
INSERT INTO `itinerary_route_info` VALUES ('2', '1', '2', '拉市海 地区', '叫醒您的可能是您的帅气或美丽，也可能是美景和期待，或者......是闹钟，但满眼都是美景的一天再次如约而至❤\r\n在丽江的早晨醒来后，您的旅程开始了，早上用过早餐之后，乘车前往【拉市海】地区。这里是美国候鸟基地，也是丽江的文化的源头，更是当年茶马古道上的重镇，在这里生活的都是纯朴的纳西民族，您可以升级骑马，体验马帮历史文化，寻找心灵的震撼。', null, null);
INSERT INTO `itinerary_route_info` VALUES ('3', '1', '2', '玉龙雪山', '用过中餐之后，您将前往丽江人们心中的圣山：【玉龙雪山】。玉龙雪山是中国南、北半球南的雪山，也是横断山脉沙鲁里山南段的名山。玉龙雪山高海拔5596米，雪山的13峰终年积雪不化，俯瞰整个丽江。', 'rlxs.jpg', 'rlxs2.jpg');
INSERT INTO `itinerary_route_info` VALUES ('4', '1', '2', '蓝月谷', '【蓝月谷】,其前身为早前人们所熟知的\"白水河\"蓝月谷又名白水河，在晴天时，水的颜色是乳白色的，阴雨天是天蓝色，而且山谷呈月牙形，远看就像一轮蓝色的月亮镶嵌在玉龙雪山脚下，所以名叫蓝月谷。 玉龙雪山冰雪融化成河水从雪山东麓的一条山谷而过，因月亮在蓝天的映衬下倒影在蓝色的湖水中，又因英国作家瞻姆斯希尔顿笔下的《消失的地平线》中的蓝月亮山谷近似于此。故名\"蓝月谷\"。 蓝月谷中的河水在流淌过程中因受山体阻挡，形成了四个较大的水面，人称\"玉液\"湖、\"镜潭\"湖、\"蓝月\"湖和\"听涛\"湖。湖岸四 周植被繁茂，远处雪峰背衬。湖水是透明的蓝，近乎凝固的湛蓝中，些许的绿意点缀其间。于湖心四顾，白云连横，浮于山际，倒影在湖面，如梦幻影，疑是仙境', 'lyh.jpg', 'lyh2.jpg');
INSERT INTO `itinerary_route_info` VALUES ('5', '1', '2', ' 束河古镇', '束河古镇，纳西语称“绍坞”，因村后聚宝山形如堆垒之高峰，以山名村，流传变异而成，意为“高峰之下的村寨”，是纳西先民在丽江坝子中较早的聚居地之一，是茶马古道上保存完好的重要集镇，也是纳西先民从农耕文明向商业文明过渡的活标本，是对外开放和马帮活动形成的集镇建设典范。\r\n\r\n民居是束河古镇建筑群极为重要的组成部分，三坊一照壁是纳西族民居典型的构造布局，小院种花，大院植树，四季花果飘香，有甚者引水入院，蓄水成池，配以亭台，就成了一幅雅致的风景。现在古镇内的民居多修缉为客栈，在保留当地木材结构的基础上，又配以玻璃材质，从而大大提高了房间的采光度，使得住宿更为舒适。', 'shgz.jpg', 'shgz2.jpg');
INSERT INTO `itinerary_route_info` VALUES ('6', '1', '3', ' 蝴蝶泉', '【大理蝴蝶泉】、蝴蝶泉是白族人的心中一个象征爱情忠贞的泉，蝴蝶泉的特色，概括起来有“三绝”：泉、蝶、树。蝴蝶泉为方形泉潭。泉水清澈如镜。在泉池西北角的池边有一棵苍劲的夜合欢古树，枝叶婆娑，树荫遮天蔽日，这就是蝴蝶树，横跨泉上，每当春末夏初，古树开花，状如彩蝶，且散发出诱蝶的清香味，其时蝴蝶群集飞舞，一只只“连须钩足”，从枝头悬至泉面，形成千百个蝶串，象一条条五彩缤纷的彩带。', 'htc.jpg', 'htc2.jpg');
INSERT INTO `itinerary_route_info` VALUES ('7', '1', '3', '洱海大游轮', '前往游览高大上的【洱海大游轮】，洱海一游是在大理所有景点超值较美的景点，游船的项目很多。风景很漂亮，游船很舒服，一路很好玩。天空很美，一路的风光可360度无死角的完美欣赏，游轮上特别赠送白族三道茶迎宾歌舞表演及品尝白族三道茶一苦、二甜、三回味。', 'prdyl.jpg', 'prdyl.jpg');
INSERT INTO `itinerary_route_info` VALUES ('8', '1', '3', '洱海骑行', '入住酒店之后免费提供脚踏车，找辆脚踏车与您的家人或伴侣沿着酒店前的骑行道，慢慢的，慢慢的在海边骑行，碧空如洗，暖风如玉，勤劳热情的白族百姓忙碌于田间，微风和着清新的空气迎面而来，还有三三两两的人们在洱海边嘻笑拍照，如此唯美而暖心的画面必将永存您的心间…', 'qx.jpg', 'qx2.jpg');
INSERT INTO `itinerary_route_info` VALUES ('9', '1', '4', '大理古城', '美好的一天仍然在大理晴朗清新的早晨中开始，希望您被大理的美景叫醒，开启美好的一天。\r\n在酒店用过早餐之后，乘车前往【大理古城】。\r\n\r\n大理古城始建于明洪武十五年（公元1382年），又名叶榆城、紫禁城、中和镇。古城外有条护城河，城内街道为典型的棋盘式布局，古城的建筑为清一色的青瓦屋面，显得十分的古朴。您可以在大理的青石板路上，漫步在大理古城，更接近蓝天和白云的地方，一定会有好运相伴，各色的小吃和各色的店铺琳琅满目，您如果需要购买一些伴手礼带回去，一定要货比三家理智消费哦。', 'dalgc.jpg', 'dalgc2.jpg');
INSERT INTO `itinerary_route_info` VALUES ('10', '1', '4', '洋人街', '在古城，有一条充满艺术气息的古街道，坐落在古城的步行街内，叫【洋人街】，\r\n大理洋人街原名“护国路”，意为民国初云南人民反对袁世凯称帝，起兵护国而得名。为了适应开放旅游的需要，把大理古城定为开放旅游的主要景点，红山茶宾馆定为涉外宾馆，红山茶宾馆在古城护国路西段，由此，护国路便成为外国游客在大理的集散地。\r\n\r\n\r\n\r\n洋人街给人的感觉，让人感觉身处大理，又会让人忘记身在何处，街边的小酒吧和特色的小吃店铺，既有大理特有的建筑风格和古朴的大理风情，又有一丝异国他乡的新鲜气息。', 'yrj.jpg', 'yrj2.jpg');
INSERT INTO `itinerary_route_info` VALUES ('11', '1', '5', '崇圣寺三塔文化旅游区', '大理标志性建筑物-【崇圣寺三塔】（电瓶车费用不含，请自行购买，参考价35元/人）：\r\n\r\n1、是南诏国和大理国时期建筑的皇家寺院，至今已有1000多年的历史。\r\n\r\n2、这里东临洱海，西靠苍山应乐峰，风景十分优美，这湖中的三塔倒影，是大理标志的景点。\r\n\r\n3、三塔是了解大理国文化的佳地，1000位游客眼中有1000个三塔，你眼中的三塔是什么样子呢。', 'csss.jpg', 'csss2.jpg');
INSERT INTO `itinerary_route_info` VALUES ('12', '1', '5', '机场', '这一天是您回程的时间，我们没有做行程安排，您可以根据您的航班时间自行安排时间和活动，送机的师傅会提前一天与您联系，告知您前往机场的时间，您就根据时间自行安排。\r\n旅程总有一个告别的时间，不论您将回到哪里，何时会再与云南相见，我们还是诚挚的希望您，期望而来，喜悦而归，愿您一路平安，大美云南，再次恭候您的到来❤', 'ljjc.jpg', 'ljjc2.jpg');
INSERT INTO `itinerary_route_info` VALUES ('17', '23', '1', '【自选航班】家门口出发  天使之城-曼谷', '  您在出行前请仔细检查携带好我们微信管家提前告知的必备物品哦！  贵宾根据航班时间（请至少提前3小时抵达机场）找到航司所在的办票柜台，自行办理登机手续后乘搭国际航班前往泰国首都—【曼谷】  抵达泰国机场之后，请自行办理落地签/或自备签证，入境泰国后送至酒店休息，养精蓄锐迎接精彩的明天！  【敬请知悉】（酒店下午2点后才可以办理入住手续）', null, null);
INSERT INTO `itinerary_route_info` VALUES ('18', '23', '2', ' 丹嫩沙朵水上市场', '丹嫩沙多水上市场的存在，也许就是让您来感受曼谷过去“古风古意”。船上买水果、河边买布衣，我们第二天行程需早上8点集合前往丹嫩沙朵水上市场（不含坐船费用，客人如需坐船游览，需自理）（丹嫩沙朵水上市场拥有百年历史，上午当地是非常热闹，带您体验泰国本土风情，水上市场12点就会关闭，我们得早早准备出发哦），为了您有充沛的状态参加游玩行程，保证充足的睡眠时间，请您选购机票时注意抵达时刻，尽量选择出发当天23点前抵达曼谷的航班哦！（请选择落地时间为当天的航班，若是第二天凌晨后抵达，长途飞机后能在酒店休息的时间非常短暂哦！敬请知悉！）', 'tg1.jpg', 'tg2.jpg');
INSERT INTO `itinerary_route_info` VALUES ('19', '23', '2', '美攻铁道市场', '美功菜市场（Mae Klong Market），位于泰国曼谷西部。这个堪称世界上危险的菜市场虽然出售的只是当地常见的粮油蔬果，但由于有一辆通勤火车每天从菜摊之间不足2米的空隙中驶过8次，它成为泰国一个有名的市井风情胜地。', 'tg3.jpg', 'tg4.jpg');
INSERT INTO `itinerary_route_info` VALUES ('20', '23', '2', '人妖表演', '随后我们前往参观【人妖表演】，这里是知名的人妖秀场，是游客爱观看的人妖秀之一，主要以歌舞表演为主题！秀场表演曾在泰国人妖大赛中名列前茅！表演可看性非常高，而且您还可支付一些小费与佳丽们合影留念。', 'tg5.jpg', 'tg6.jpg');
INSERT INTO `itinerary_route_info` VALUES ('21', '23', '3', '大皇宫', '紧邻湄南河，是曼谷中心内一处大规模古建筑群，总面积二十一万八千四百平方米。大皇宫是仿照故都大城的旧皇宫建造的。大皇宫是泰国诸多王宫之一，是历代王宫保存完美、规模大、有民族特色的王宫。现在，大皇宫除了用于举行加冕典礼、宫廷庆祝等仪式和活动外，平时对外开放，成为泰国著名的游览场所。', 'tg.jpg', 'tg8.jpg');
INSERT INTO `itinerary_route_info` VALUES ('22', '23', '3', '玉佛寺', '玉佛寺（Wat Phra Kaew）位于泰国曼谷皇宫内，与皇宫同建于1789年。泰国玉佛寺位于曼谷大王宫的东北角，是泰国著名的佛寺，也是泰国三大国宝之一。作为皇室举行宗教仪式的地方，是曼谷重要的寺庙，由拉玛一世在兴建皇宫时一并建造，并于1784年3月27日迎请玉佛到寺中供奉。', 'tg9.jpg', 'tg10.jpg');
INSERT INTO `itinerary_route_info` VALUES ('23', '23', '3', '泰服体验', '作为新潮的您，怎么能错过异国风情体验：穿上传统泰服，拍各种美照，发抖音，发微信，刷爆亲朋好友圈。收获满满点赞。 ', 'tg12.gif', 'tg11.gif');
INSERT INTO `itinerary_route_info` VALUES ('24', '23', '4', ' 沙美岛', '用过早餐后，前往位于罗永府的【沙美岛】。岛上有黄里透白，面粉一样细嫩的海沙，铺成又长又宽的沙毯，  为太平洋镶上了金色的花边。慵懒的躺在沙滩上沐浴阳光，沙美岛海滩上踏浪、漫步，尽情享受南国热带的海洋魅  力，放松自我、忘掉一切世俗尘嚣，尽享这世外桃源寄予的难得假期。', 'tg14.jpg', 'tg13.jpg');
INSERT INTO `itinerary_route_info` VALUES ('25', '23', '4', '暹罗传奇', '之后集合乘车返回芭堤雅，前往【暹罗传奇】泰文化主题园，暹罗传奇耗资40亿泰铢打造，是泰国规模很大、全方位展示泰国文化的主题乐园，在这里，古暹罗各时期的传奇和文明精髓都将以新奇、梦幻的方式重现，致力于为每一位到访游客打造泰式风情的极 致体验！', 'tg15.jpg', 'tg16.jpg');
INSERT INTO `itinerary_route_info` VALUES ('26', '23', '5', '参拜四面佛', '早餐后出发乘车返回曼谷，经过芭提雅有名的四面佛每日都有很多来自世界各地的信众前往参拜祈福。四面佛掌管人间的所有事情，故此其四面所求都不相同。', 'tg17.jpg', 'tg18.jpg');
INSERT INTO `itinerary_route_info` VALUES ('27', '23', '5', '温馨的家', '今日我们会根据您的航班时间，安排送机服务，送您至对应的机场，搭乘航班返回国内，并于泰国机场散团，结束泰国之行，期待下一次您的泰国之旅！', null, null);
INSERT INTO `itinerary_route_info` VALUES ('28', '24', '1', '【专属启程】全国-普吉岛（专车接机）', '【今天是出发日】【专车接机-泰语司机】  ★您的航班由途牛系统默认匹配航班,如您对航班有特殊要求,请选择出发日期后,点击下一步选择资源\'重选航班\'.根据所选航班,按时抵达机场办理登机手续  ★若自选航班需在中转地(例如香港)停留一晚的,请自行确认中转地留宿酒店/通行证/第三国签证,费用自理.  ★建议您选择匹配直飞航班,体验更好的旅途;若您选择匹配转机航班,建议选择在3-5小时以内转机航班', null, null);
INSERT INTO `itinerary_route_info` VALUES ('29', '24', '2', ' 尼莫海豚馆', '与海豚嬉戏', 'tgg2.jpg', null);
INSERT INTO `itinerary_route_info` VALUES ('30', '24', '2', '普吉镇', '普吉镇位普吉府的省会，有中国和葡萄牙特色的多元化老建筑、老爷车、老式巴士，完好保留了过去商贸发达时的殖民风情。街上有画廊、酒吧、咖啡馆等，颇具氛围和情调。', 'tgg3.jpeg', 'tgg4.jpeg');
INSERT INTO `itinerary_route_info` VALUES ('31', '24', '2', '考朗山', '考朗山就位于普吉镇附近，说是山但其实并不是很高，不过也是普吉岛的一个比较重要的观光景点，从山顶可以鸟瞰整个普吉市区普吉南部地区以及一些离岸岛屿的美景。山顶上还提供野餐设施的公园以及一些气氛环境很好的饭馆，可以一边用餐，一边欣赏风景，每天夕阳西下的时候，这里也是一个不错的欣赏日落地点。', 'tgg5.jpeg', 'tgg6.jpeg');
INSERT INTO `itinerary_route_info` VALUES ('32', '24', '3', '明星快艇 Sea star 皮皮岛竹子岛浮潜一日游 可换购大小皇帝岛+蜜月岛双体动力快艇一日游', '【参考行程】  以下行程安排仅供参考，接人时间请以确认函为准，导游会根据当天风浪、潮汐等因素微调行程，敬请理解！  07:30 司机酒店接您，开始一天的行程  09:00 前往皮皮岛  10:00 到达竹岛进行畅游和浮潜  12:00 到达大皮皮岛通塞湾登岛享用午餐  13:30 观赏燕窝洞, 游览猴子沙滩、罗萨马湾  14:00 到达玛雅湾，在这尽情的放松、畅游、浮潜  15:00 离开玛雅湾开始返程  16:30 到达 Royal Phuket Marina 码头，返回您的酒店', 'tgg7.jpg', 'tgg8.jpg');
INSERT INTO `itinerary_route_info` VALUES ('33', '24', '4', '专车换酒店', '今天是全天自由活动，可自由享受酒店内设施~  13:00专车前往新酒店   【可选升级包】  升级包A：【迷失绿洲】-迷失绿洲套餐2小时【买一送一】（芭东、卡塔、卡伦3大海滩免费接送）  需补差价：915/份  仅限12-65周岁；（2成人拍1份即可，默认场次：10：00，20：00）  温馨提示：12岁以下的儿童因骨骼仍在成长阶段，为避免影响，不建议参加正宗泰式按摩项目  升级包B：【丛林飞跃（16站，含接送)】（芭东、卡塔、卡伦3大海滩免费接送）  全程游玩时间约1小时30分钟。  场次：08:00/15:00（限12-60周岁，120公斤以下人群）  需补差价：325元/成人/人  升级包C：【幻多奇乐园】-【表演+普通餐+黄金座+接送】（周四休业不开放）  需补差价：430元/成人，400元/儿童', 'tgg10.jpeg', 'tgg9.jpeg');
INSERT INTO `itinerary_route_info` VALUES ('34', '24', '5', '【全天自由活动】 惊喜1元礼包二选一（不可同享）', '【全天自由活动】  全天自由活动  今天是一个没有morning call的早晨，一觉睡到自然醒。  您可以自由地享受美好时光，泡酒店或是和家人朋友DIY个性行程。   1元蜜月礼包二选一（不可同享）  （1）兰花泰式按摩120分钟-含接送  可选场次 11:00/14:00/20:00  (如无备注，默认安排15:00场次）  （2）丛林ATV60分钟  可选场次08:15/13:15（如无备注，默认安排在08:15）  *以上升级包如无备注，默认安排兰花泰式按摩', 'tgg11.jpeg', 'tgg12.jpeg');
INSERT INTO `itinerary_route_info` VALUES ('35', '27', '1', ' 丽江三义机场', '今日行程：自行安排前往丽江——丽江三义机场举客人姓名接机——送至酒店入住——可自行安排游览【丽江古城】。', 'yun2.jpg', 'yun3.jpg');
INSERT INTO `itinerary_route_info` VALUES ('36', '27', '2', ' 玉龙雪山', '有人说，丽江是一种情怀，也有人说，丽江是一种得不到的感情寄托，丽江是自由的，而丽江的【玉龙雪山】却是神秘的，因为在纳西族人心中，玉龙雪山是保护神般的存在，每个来丽江的游客，都会去亲临这座巍峨的山川，体验异域风情，感受神山的魅力。', 'yun4.jpg', 'yun5.jpg');
INSERT INTO `itinerary_route_info` VALUES ('37', '27', '2', '云杉坪小索道', '乘坐【云杉坪索道】可以抵达海拔3240米隐藏在原始云杉林中的云杉坪，漫步在云杉坪的人行栈道上，两旁或高耸，或颓败的参天古树木，树叶绿油油的，拍出的照片非常好看。下索道后穿过一片原始森林即可到达雪山半山腰拍摄雪山全景哦。', 'yun6.jpg', 'yun7.jpg');
INSERT INTO `itinerary_route_info` VALUES ('38', '27', '2', '自由活动', '乘坐【冰川公园大索道】到达的是雪山的主峰，从海拔3356米的下部站直达海拔4506米的冰川公园，海拔落差1000多米。从冰川公园处沿着一段栈道步行，便能看到4680的标志性石柱，《极限挑战》在玉龙雪山录制时，一众明星们也曾在此地打卡哟。', 'yun8.jpg', 'yun9.jpg');
INSERT INTO `itinerary_route_info` VALUES ('39', '27', '3', '蓝月谷', '乘坐景区环保车即可到达有“小九寨”之称号的【蓝月谷】，在晴朗的天气下，湖水是一种淡淡的、清透而优雅的蓝色，山谷呈月牙形，远看就像一轮蓝色的月亮镶嵌在玉龙雪山脚下非常适合拍照，所以不少新人都会在这里拍婚纱写真的哦！ ps：蓝月谷电瓶车参考价格约50元/人，敬请自理。', 'yun10.jpg', 'yun11.jpg');
INSERT INTO `itinerary_route_info` VALUES ('40', '27', '3', '香格里拉市', '结束丽江玉龙雪山行程后，乘车前往云南的藏区——迪庆州香格里拉市，行驶在214国道，一路观高山峡谷风光，途经高原牧场，领略祖国大好壮丽河山。', 'yun12.jpg', 'yun13.png');
INSERT INTO `itinerary_route_info` VALUES ('41', '27', '3', '独克宗古城', '自由活动时间可自行前往游览独克宗古城。 被称为“月光之城”的【独克宗古城】具有1300多年历史，曾是茶马古道上由滇入藏的重要驿站，古城中心龟山上有一需多人共同使力才能转动起来的巨大的转经筒。走在独克宗的街头，尤其是夜晚，路经载歌载舞的四方街后，忽又转入一安静的独具民族特色的古屋古街的小巷子，立马会产生穿越时空的恍惚感。', 'yun14.jpg', 'yun15.jpg');
INSERT INTO `itinerary_route_info` VALUES ('42', '28', '1', ' 入住酒店 - 市区首日自由行', '已经预定此线路的朋友，今天要带着家人和朋友，自行前往火车站办理乘车手续，然后前往这次的旅游目的地【桂林，具体车次以及发车时间以出团通知书为准！若所出车次不能完全满足您的要求，敬请谅解！出游日前一天22:30前会有导游联系，请保持手机畅通，如21:30-22:30此时间段无法联系，请预定时提前告知！根据火车抵达的时间，专门的接站师傅会在客人到站前提前联系客人，并在车站等候您一起前往酒店。导游将在此日19：00前联系通知出游时间和地点，还请保持电话畅通，请注意查收短信或接听导游电话。', null, null);
INSERT INTO `itinerary_route_info` VALUES ('43', '28', '2', '5A漓江精华游', '早餐后乘车前往阳朔兴坪古镇漓江码头，乘船游览【兴坪渔村】含船票（20元人民币背景精华段，乘船游览约60分钟 7、8月旺季期间，如遇兴坪船限号，则为您升级为漓江竹筏）。漓江两岸自群峰竞秀、碧水球流，沿岸有溜圆扭卷的螺蛳山、挺肚憨笑的罗汉山、一僧一尼正面对面斗嘴的僧尼山。沿途欣赏绝美景色：连绵的山光水色，山水相间的和谐，山与水之间，夹杂了人村田园，沿江翠竹掩映，炊烟袅袅；所经之处皆是农村地带，山野自然趣味，宁静空旷，风光历历在目。', 'gl.jpg', 'gl2.jpg');
INSERT INTO `itinerary_route_info` VALUES ('44', '28', '2', ' 兴坪古镇', '兴坪素以山水秀丽、景甲天下而著称，是中国旅游名阳朔县的旅游重镇。兴坪古镇位于阳朔东北部，距县城25公里，漓江在此绕了一个大弯。兴坪依山傍水，风景荟萃，粉墙乌瓦石板小巷，是漓江沿岸一座美丽的古镇。兴坪素以山水秀丽、景甲天下而著称，是中国旅游名县阳朔县的旅游重镇。位于阳朔县城东北部，距阳朔县城25公里。世界著名的漓江蜿蜒流于该镇西南部，境内江段长达20多公里，两岸群峰连绵，如剑芒排扦，奇特怪异，万态千姿，绿水滢回，青山环列，倒影幢幢，翠竹成林，垂柳如茵，泛舟江心帆星点点，相映成趣。兴坪，桂林山水，这里有著名的九马画山、黄布倒影、僧伲斗嘴、朝板山、榕潭揽胜、雾绕青螺，这里有两个总统游过的神秘、古朴村落——渔村，这里有堪称世界岩溶奇观的——莲花岩，这里有被印制在1999年版贰拾元人民币背景图案上的兴坪大河背秀丽风景。古往今来，兴坪秀丽的山山水水，引得无数骚人墨客为之陶醉。老一辈无产阶级革命家叶剑英元帅曾在1963年畅游兴坪时写下了春风漓水客舟轻，夹岸奇峰列送迎。马跃华山人睇镜，果然佳胜在兴坪。的不朽诗篇。', 'gl.jpg', 'gl2.jpg');
INSERT INTO `itinerary_route_info` VALUES ('45', '28', '2', ' 刘三姐大观园', '刘三姐大观园坐落在美丽的桃花江畔，园内有侗家鼓楼、刘三姐故居等景观，每天还有多场少数民族表演，浓缩了多个民族的文化风情。除了欣赏大观园内优美的山水园林景观，还可以和身着民族服饰的阿姐一起对歌、跳竹竿舞，或者参与抛绣球、民族婚礼等环节，身临其境地体验民族风情', 'gl.jpg', 'gl2.jpg');
INSERT INTO `itinerary_route_info` VALUES ('46', '28', '3', ' 少数民族观光村', '【少数民族观光【少数民族观光寨】（约90分钟）感受少数名族吊脚楼，原生态的少数名族原住民风情，不宽的街道上铺着青板，石板路两边是完好的名族全木式建筑，感受少数名族的风土人情和村民古老、恬静、悠慢的生活。古寨景点中带有少数民族传统手工制作银饰展示，属于景区经营行为，故购买需游客根据自己的喜好和意愿理性消费。', 'gl.jpg', 'gl2.jpg');
INSERT INTO `itinerary_route_info` VALUES ('47', '28', '3', ' 十里画廊', '遇龙河是漓江流经阳朔一条支流，水流平缓，清澈见底，乘坐竹筏顺流而下，便可感受到“舟行碧波上，人在画中游”的唯美意境。其两岸山峦百态千姿，河畔翠竹叠嶂，蕉林四季常青，沿岸地稻田一年四季颜色不断变化，美似一幅画卷。许多婚纱外拍和美术写生都会选择在这里取景，是阳朔旅游必不可少的游览景点。', 'gl.jpg', 'gl2.jpg');
INSERT INTO `itinerary_route_info` VALUES ('48', '28', '3', '桂林千古情演出', '桂林千古情景区由宋城演艺和桂林旅股联合打造的桂林千古情景区，再现了桂林的历史文化与民族风情，老少同乐、晴雨皆宜。核心演出大型歌舞《桂林千古情》是一生必看的演出，心灵震撼，唱响了一曲感天动地的桂林千古传奇。  一切以游客为中心，拥有上万平米全室内空调开放的3号秘境旅游综合体、风雨廊、五~星~级标准洗手间。巨型歌仙造像气势宏伟，与桂林山水融为一体，是八桂大地真善美儿女的化身。景区内鬼屋、活着的清明上河图、奇妙街等数十项高科技和儿童体验项目妙趣横生。阳朔古村营造了独特的农耕市井生活风貌，市井街、风情街内爷爷的酒缸、奶奶的糕点、爸爸的玩具等手工作坊留住乡愁、寻找父辈的记忆。东街云集了湘菜、粤菜等全国特色美食和小吃。情人港、4号海湾是年轻人休闲的好去处。不能错过的就是精彩指数七星级的大型歌舞《桂林千古情》，还有五~星~级的5D实景剧《山崩地裂》，以及《锅庄狂欢》《漓江渔火》《民族快闪秀》等数十大演艺秀和科技秀，感受一场文化与科技的震惊演出，让你流连忘返。  还有即将开放的不夜城酒吧街，与千古情景区一起成为阳朔真正的不夜城。给我一天，还你千年！', 'gl.jpg', 'gl2.jpg');
INSERT INTO `itinerary_route_info` VALUES ('49', '28', '3', '天下奇观—银子岩', '桂林荔浦银子岩旅游景区位于桂林市荔浦县马岭镇荔桂公路东侧，距世界旅游明珠桂林市中心85公里，距中国旅游名县阳朔县界3公里，是大桂林旅游圈新出现的一颗璀璨明珠。银子岩，去了银子岩，一世不缺钱游览【世界溶岩奇观——银子岩】，桂林绝美岩洞，洞内汇集了高达数十米，雄、奇、幽、美，像银子似钻石的钟乳石，特色景点以音乐石屏、广寒深宫、雪山飞瀑为三绝，佛祖论经、独柱擎天、混元珍珠伞为三宝作为代表，令人流连忘返，美国前总统克林顿也是它的粉丝， 98年访华时为了游览银子岩专门推迟了去香港的班机！', 'gl.jpg', 'gl2.jpg');
INSERT INTO `itinerary_route_info` VALUES ('50', '28', '4', ' 訾州·象山', '訾洲是漓江上一座绿洲，它位于桂林城东漓江与漓江叉河之间，訾洲碧水环绕，群山簇拥，每到秋天，洲上苍翠之中，红黄秋叶翩翩，\"訾洲红叶桂林秋\"为桂林胜景。訾洲呈条形如舟浮水中，漓水环绕，在訾洲观景台上以*近、*清晰的角度欣赏到桂林城徽象鼻山；东可望七星山、穿山、塔山，西可观叠彩山、伏波山、独秀峰、雉山、南溪山、斗鸡山；若恰好是在烟雨弥漫的时节游览，云纱雾幔将訾洲笼罩在一片朦胧之中，犹如一幅水墨画卷，充满诗情画意，这就是誉满八桂的“訾洲烟雨”美景。', 'gl.jpg', 'gl2.jpg');
INSERT INTO `itinerary_route_info` VALUES ('51', '28', '4', '特别安排漓江边休闲下午茶', '特别赠送下午茶，旅行的过程并不一定匆忙而过，可以脚步放慢，尽享我们精心为之安排的悠闲【下午茶】，感受景在城中，人在景中的意境，在这样一个下午，一壶茶、一份糕点、 一首歌，平平淡淡，简简单单，旅途的倦意，过眼烟云，时光安好。', 'gl.jpg', 'gl2.jpg');
INSERT INTO `itinerary_route_info` VALUES ('52', '28', '4', '温馨的家', '【送站服务】：一次性送站服务（即根据行程结束后车次安排集合时间一同前往列车站办理乘车手续），会提前一个小时送各位贵宾前往车站候车，如果这个时间您感觉较早也可继续自由活动然后自行前往列车站，费用自理。结束快乐的旅程！旅游旺季车票比较紧张，我们在保证行程能够游览结束的前提下安排，可能会出现较晚车次，带来不便敬请谅解！  (如返程需自理的游客,请预订17:00以后的车次返回,以免误车.谢谢配合)', '', '');
INSERT INTO `itinerary_route_info` VALUES ('53', '29', '1', '【白水寨】吃中餐', '11;00到达【白水寨】吃中餐（中午AA每人35-40元左右或自己解决）', 'gl.jpg', 'gl2.jpg');
INSERT INTO `itinerary_route_info` VALUES ('54', '29', '1', '增城白水寨', '白水寨风景名胜区位于增城市北部山区的派潭镇，现为省级风景名胜区，面积约170平方公里，北回归线穿越其中，被誉为北回归线上的瑰丽翡翠，属山岳型风景名胜区。', 'gl.jpg', 'gl2.jpg');
INSERT INTO `itinerary_route_info` VALUES ('55', '29', '1', '自由活动', '白水寨瀑布是国内落差较大瀑布。亲水栈道一共9999级，其中1至399级的寻仙梯分别有石马龙湿地公园（在建）、景湖、天南一梯、寿龟潭、回音壁、会仙桥6个景点；399级至1299级的怡景梯有秀色云林、聆涧洗心、神龟谷、春夏飞雪4个景点；1299级至2099级的知难梯有亲瀑台、白龙腾飞、泉声咽石3个景点；2099级至5599级的揽胜梯有指点江山、松林鸟语、仙沐阁、沐云亭、仙源公园、仙源洞6个景点；5599级至8999级的勇士梯有白水寨天池、观天台2个景点；8999级至9999级的登峰梯有九重天、七仙湖、东林寺（拟建）3个景点。在5599级的松林鸟语景点，鸟语声声，环境优美，让游人心旷神怡。游人登高至8999级至9999级时可观赏到被誉为“南国天池”的白水寨天池和七星湖等景点。南国天池犹如散落在绿色群山之中的一块翡翠碧玉，绿得奇特，碧蓝如湛，春夏为雨雾烟霞笼罩，秋冬被长风搅动波澜，意境无限。在寿龟潭、神龟谷等景点，游人不仅可观赏到清澈见底的流水，还可欣赏到大自然鬼斧神工的形态各异的奇石。', 'gl.jpg', 'gl2.jpg');
INSERT INTO `itinerary_route_info` VALUES ('56', '29', '1', '回家', '17;00集合出发，返回，结束一天的快乐旅程！', '', '');
INSERT INTO `itinerary_route_info` VALUES ('57', '30', '1', ' 黄金海岸环岛游', '前往金色海岸乘豪华游艇【深港环岛游】（60分钟），饱览世界第六大港口盐田港、航母世界、大小梅沙、香港鸭州岛、吉澳岛、远眺龙吐珠，在海上观望空气清新一切超然，心情无比舒畅。清新的空气、美丽的风景，相信一定会给您留下难忘的印象。', 'gl.jpg', 'gl2.jpg');
INSERT INTO `itinerary_route_info` VALUES ('58', '30', '1', '地王观光', '后前往【地王大厦】（含顶楼观光、60分钟）地王大厦作为深圳的地标性建筑，与周围人文景观相配合，成为深圳独特的旅游景点。地王顶层的观光项目，可以俯瞰市容以“深港之窗”作主题的形象展示，将深港区域性历史文化，现代都市商业、休闲文化、高空观光的高科技文化结合为一处，开创了国内高层观光的旅游新境界。', 'gl.jpg', 'gl2.jpg');
INSERT INTO `itinerary_route_info` VALUES ('59', '30', '1', '沙头角中英街', '后前往深圳特有的、全世界特色的“一街两制”免税商业街【沙头角中英街】（约90分钟），长不足0.5公里，宽不够7米，街心以“界碑石”为界，街边商店林立，有来自五大洲的产品，品种十分齐全。因到特区的人要到深圳市公安局办理一张“前往边防禁区特许通行证”，才能进入“中英街”，故“中英街” 被称作特区中的“特区”。', 'gl.jpg', 'gl2.jpg');
INSERT INTO `itinerary_route_info` VALUES ('60', '30', '2', '二沙岛', '游览天河新貌（中信广场、市长大厦、天河体育中心、火车东站绿化广场）路观羊城新八景之一的天河飘绢（火车东站水景瀑布），乘车往【二沙岛】，外观体现广州精神文明建设标志性建筑的-【星海音乐厅】', 'gl.jpg', 'gl2.jpg');
INSERT INTO `itinerary_route_info` VALUES ('61', '30', '2', '白云山', '游览国家5A级风景名胜游览区【白云山】（不含含电瓶车及缆车60元，约60分钟）被称为“羊城8秀”，由30多座山峰组成，登高可俯览全市，遥望珠江。每当雨后天晴或暮春时节，山间白云综绕，山名由此得来。', 'gl.jpg', 'gl2.jpg');
INSERT INTO `itinerary_route_info` VALUES ('62', '30', '2', ' 黄埔军校旧址', '前往参观世界著名四大军校之一的【黄埔军校】（约40分钟,逢周一闭馆）—它是大革命时期孙中山在中国共产党和苏联的助下建立的一所新型军事学校。1984年建立黄埔军校旧址纪念馆，现在军校旧址的纪念建筑有军校正门、中山纪念碑、中山故居、俱乐部、游泳池及东征烈士墓等。黄埔军校旧址和校史，生动地展示了一次国共合作时期国共两党携手进行反帝反军阀斗争的历史', 'gl.jpg', 'gl2.jpg');
INSERT INTO `itinerary_route_info` VALUES ('63', '30', '2', '陈家祠', '参观“岭南建筑艺术明珠”--【陈家祠】（游览时间约60分钟）----光绪十四年（1888年）筹建，光绪十二年（1894年）建成，历时七年。因祠堂落成后，一直作为陈姓子弟读书办学的地方，故又称陈氏书院。光绪三十一年（1905年）废科举后，书院改为陈氏实业学堂。1950年设立广州市行政干部学校。1957年，经广州市人民委员会批准列为广州市文物保护单位。1959年辟为广东民间工艺馆。1960年经省人民政府批准列为广东省文物保护单位。1988年由国务院颁布为全国重点文物保护单位。陈氏书院坐**南，门前有开阔的广场，设有前、后、东、西四院，占地面积13200平方米。建筑外围有青砖围墙，形成一座外封闭内开放的建筑群体，是典型的广东民间宗祠式建筑。 ', 'gl.jpg', 'gl2.jpg');
INSERT INTO `itinerary_route_info` VALUES ('64', '30', '2', ' 越秀公园', '游览【越秀公园】（约20分钟）、是新世纪羊城八景之一，如一艘古老的游船，承载着悠悠的广州历史，从远古驶到现在。其主山越秀山虽历经沧桑，但弦歌不辍。自元代以来，其风景在各时期的羊城八景中占有重要的位置。尤其是在构建和谐社会的今天，越发展现出其迷人的风采。它不仅保存了镇海楼、明代古城墙、四方炮台遗址、中山纪念碑等各个历史时期的遗迹和众多的古树名木，还建设了五羊仙庭、成语寓言园、竹林休闲区、海东京畿园及游泳场等。历史古迹与现代文明齐辉，自然造化与人工巧设共舞，相映成趣。游毕在此统一散团，结束愉快行程！', 'gl.jpg', 'gl2.jpg');
INSERT INTO `itinerary_route_info` VALUES ('65', '32', '1', '出发地-北京', '抵达北京后，由司机师傅接机/站，送至酒店，请您按导游提示信息自行到前台办理入住，之后自由活动。', '', '');
INSERT INTO `itinerary_route_info` VALUES ('66', '32', '2', '天安门广场', '前往城市中心广场——【天安门广场】，位于北京市中心，南北长880米，东西宽500米，面积达44万平方米，可容纳100万人举行盛大集会。天安门广场曾是封建帝王统治时代紫禁城正门外的一个宫廷广场，东、西、南三面用围墙围成一片的普通百姓的禁地。', '', '');
INSERT INTO `itinerary_route_info` VALUES ('67', '32', '2', '人民英雄纪念碑', '【人民英雄纪念碑】位于北京天安门广场中心。1949年9月30日，中国人民政治协商会议第1届全体会议决定，为了纪念在人民解放战争和人民革命中牺牲的人民英雄，在首都北京建立人民英雄纪念碑。当天下午6时，出席中国人民政治协商会议的全体代表，在天安门前广场上举行了建立纪念碑的奠基典礼。后经全国广泛讨论，确定碑型。1961年，人民英雄纪念碑被中华人民共和国国务院公布为第1批全国重点文物保护单位之一。人民英雄纪念碑位于毛主席纪念堂以北，是中华人民共和国政府为纪念中国近现代史上的革命烈士而修建的纪念碑。', 'renmen.jpeg', 'tam.jpeg');
INSERT INTO `itinerary_route_info` VALUES ('68', '32', '2', '毛主席纪念堂', '【毛主席纪念堂】位于天安门广场人民英雄纪念碑南面。1977年5月落成，占地57000多平方米，总建筑面积为28000平方米。主体呈正方形，外有44根福建黄色花岗石建筑的明柱，柱间装有广州石湾花饰陶板，通体青岛花岗石贴面。屋顶有两层玻璃飞檐，檐间镶葵花浮雕。基座有两层**，台帮全部用四川大渡河旁的枣红色花岗石砌成，四周环以房山汉白玉万年青花饰栏杆。 随后外观【人民大会堂】、【国家博物馆】、【国家大剧院】。', 'renmen.jpeg', 'tam.jpeg');
INSERT INTO `itinerary_route_info` VALUES ('69', '32', '2', '故宫', '游览世界上规模较大的古代皇家宫殿建筑群——【故宫】（3小时深度游，赠珍宝馆），感受皇家宫殿的豪华气派。 故宫位于北京市中心，旧称紫禁城。于明代永乐十八年（1420年）建成，是明、清两代的皇宫，无与伦比的古代建筑杰作，世界现存较大、较完整的木质a结构的古建筑群。故宫全部建筑由“前朝”与“内廷”两部分组成，四周有城墙围绕。四面由筒子河环抱。城四角有角楼。四面各有一门，正南是午门，为故宫的正门。  “故宫上新了、赏稀世珍宝、探延禧宫略、专业导游讲解、深度游玩”——牛牛带您告别传统、拒绝走马观花！特别赠送故宫小门票【珍宝馆】，举世无双珍宝库，乾隆养老在此处。  牛牛达人的参观路线：午门—太和殿—中和殿—保和殿—延禧宫—珍宝馆（含门票）—九龙壁—乐寿堂—珍妃井—御花园—神武门（具体以实际安排为准~珍宝馆为赠送项目，如因自身原因或不可抗力原因未能参观费用不退，敬请谅解）', 'renmen.jpeg', 'tam.jpeg');
INSERT INTO `itinerary_route_info` VALUES ('70', '32', '3', ' 升旗仪式', '每天日出时的【升旗仪式】是天安门广场倍受瞩目的神圣仪式，吸引无数游人聚集于此，怀着激动的心情目视着五星红旗在天空中飘扬。', '', '');
INSERT INTO `itinerary_route_info` VALUES ('71', '32', '3', '八达岭长城', '登【八达岭长城】，观长城内外祖国大好河山，抒发“登长城做好汉”的雄心壮志~ 八达岭长城是长城建筑中十分精华的一段，集巍峨险峻、秀丽苍翠于一体，为全国十大风景名胜区之首。1972年，美国总统尼克松在长城上说，“只有一个伟大的民族，才能造得出这样一座伟大的长城”。2009年，美国总统奥巴马同样在长城上说：“在这里，我们要对中华文明表示极大的崇敬”。（游览时间约2小时）', 'renmen.jpeg', 'tam.jpeg');
INSERT INTO `itinerary_route_info` VALUES ('72', '32', '3', '外观鸟巢', '游览北京奥林匹克公园；外观2008北京奥运会主会场国家体育场—鸟巢、外观极富特色的国家游泳中心—水立方。  北京2008奥运会开幕式的体育场馆！“鸟巢”外形结构主要由巨大的门式钢架组成，共有24根桁架柱，现已完成20根桁架柱整柱及2根下柱吊装。', 'renmen.jpeg', 'tam.jpeg');
INSERT INTO `itinerary_route_info` VALUES ('73', '32', '3', '外观鸟巢', '国家游泳中心（水立方）是2008年北京奥运会标志性场馆之一，也是由港澳台侨同胞捐资建设的奥运场馆。', 'renmen.jpeg', 'tam.jpeg');
INSERT INTO `itinerary_route_info` VALUES ('74', '32', '4', ' 颐和园', '游览中国现存规模较大、保存较完整的皇家园林——【颐和园】。 赏碧波涟漪的昆明湖，观苍翠如黛的万寿山，游览佛香阁、长廊、石舫、苏州街、十七孔桥、谐趣园、大戏台等家喻户晓的代表性景点。（游览时间约2小时）', 'renmen.jpeg', 'tam.jpeg');
INSERT INTO `itinerary_route_info` VALUES ('75', '32', '4', '外观清华或北大', '【清华大学】的前身是清华学堂，始建于1911年4月26日，当时是由美国“退还”的部分“庚子赔款”建立的留美预备学校。1912年，清华学堂更名为清华学校。1925年设立大学部，开始招收四年制大学生，并开设研究院（国学门）。1928年更名为“国立清华大学”，拥有文、法、理、工等院系。  【北京大学】简称北大，诞生于1898年，初名京师大学堂，是中国近代第1所国立大学，其成立标志着中国近代高等教育的开端。', 'renmen.jpeg', 'tam.jpeg');
INSERT INTO `itinerary_route_info` VALUES ('76', '32', '4', ' 北海公园', '【北海公园】位于市中心区，在故宫的西北面，因与中海、南海分称三海而得名。这里原是辽、金、元、明、清5个朝代逐 渐修建而成的帝王宫苑，是我国现存具综合性和代表性的皇家园林之一。继承了中国历代的造园传统，博采各地造园技艺所长，兼有北方园林的宏阔气势和江南私家园林婉约多姿的风韵，并蓄帝王宫苑的富丽堂皇及宗教寺院的庄严肃穆，气象万千而又浑然一体，是中国园林艺术的瑰宝。', 'renmen.jpeg', 'tam.jpeg');
INSERT INTO `itinerary_route_info` VALUES ('77', '32', '4', ' 什刹海', '前往【什刹海风情区】和【烟袋斜街】，亲身感受冯小刚热销电影【非诚勿扰1】拍摄地，感受葛优和舒淇的恋爱。  【什刹海】是北京市历史文化旅游风景区、北京市历史文化保护区。位于市中心城区西城区，毗邻北京城中轴线。水域面积33.6万平方米，与中南海水域一脉相连，是北京内城一处具有开阔水面的开放型景区，也是北京城内面积极大、风貌保存极完整的一片历史街区，在北京城规划建设史上占有独特的地位。', '', '');
INSERT INTO `itinerary_route_info` VALUES ('78', '32', '5', ' 烟袋斜街', '【烟袋斜街】位于北京市地安门外大街鼓楼前，属西城区厂桥地界。在清末至二三十年代，街内以经营旱烟袋、水烟袋等烟具、古玩、书画、裱画、文具及风味小吃、服务行业等为主，其铺面建筑风格朴素并有北京北城特点，是北京北城较有名气的文化街，曾留下不少文化名人的足迹。', 'renmen.jpeg', 'tam.jpeg');
INSERT INTO `itinerary_route_info` VALUES ('79', '32', '5', '天坛公园', '参观世界文化遗产之【天坛公园】（含大门票），为美好的明天祈福。  它是明清两代皇帝孟春祈谷、夏三祈雨、冬至祭天的圣地。北京天坛地处原北京外城的东南部，故宫正南偏东，正阳门外东侧，始建于明朝永乐十八年（1420年），是中国古代明、清两朝历代皇帝祭天之地。（游览时间约1小时）', 'renmen.jpeg', 'tam.jpeg');
INSERT INTO `itinerary_route_info` VALUES ('80', '32', '5', ' 前门大街', '前往改造一新的【前门商业街】，感受老北京二三十年代的风情，逛全聚德烤鸭店、都一处烧麦店、月盛斋等百年老店，感受不一样的北京韵味。 前门大街是北京非常著名商业街。位于京城中轴线，北起前门月亮湾，南至天桥路口，与天桥南大街相连。明嘉靖二十九年（1550）建外城前是皇帝出城赴天坛、山川坛的御路，建外城后为外城主要南北街道。民众俗称前门大街。大街长845米，行车道宽20米。明、清至民国时皆称正阳门大街。1965年正式定名为前门大街。（游览时间约1小时）', 'renmen.jpeg', 'tam.jpeg');
