-- ----------------------------
-- Table structure for `notice_of_reservation_info`
-- ----------------------------
DROP TABLE IF EXISTS `notice_of_reservation_info`;
CREATE TABLE `notice_of_reservation_info` (
  `xzid` int(100) NOT NULL AUTO_INCREMENT,
  `xztitle` varchar(30) COLLATE utf8_unicode_ci DEFAULT NULL,
  `xztext` varchar(10000) COLLATE utf8_unicode_ci DEFAULT NULL,
  PRIMARY KEY (`xzid`)
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

-- ----------------------------
-- Records of notice_of_reservation_info
-- ----------------------------
INSERT INTO `notice_of_reservation_info` VALUES ('1', '注意事项', '预订时请告之您的出游人数、出发日期、住宿、用餐标准、以及您的特殊需求。 团队报价按2人入住1间房核算，如出现单男单女，则尽量安排与其他同性别团友拼房或加床；若客人无需安排或旅行社无法安排，请补齐单房差以享用单人房间。\r \r 团队机票一经开出，不得更改，不得签转，不得退票；另飞行时间、车程时间、船程时间以当日实际所用时间为准。如遇国家或航空公司政策性调整机票、燃油税价格，按调整后的实际价格结算。当地购物时请慎重考虑，把握好质量与价格，请务必保存好所有的购物票据，若购物点提供发票，请索要。\r \r 本公司会于出发前20个工作日通知取消该行程，您可以选择延期出发、更改线路出行，或退回团款。 团队游览中不允许擅自离团（自由活动除外），如有不便敬请谅解。');
INSERT INTO `notice_of_reservation_info` VALUES ('2', '特别约定', '因甲方原因发生拒签、延时交签造成本次出境行程变更或取消的，乙方不承担违约责任，甲方需承担申办签证费用____/人。\r\n本团机位订金____元/人，未送签证前，如因甲方原因取消行程的，已收取的机位订金不予退还，同时按照旅游合同相关条款保留收取甲方违约金的权利。\r\n因甲方原因送签后或者出签后造成本次行程变更或取消行程的，乙方不承担任何违约责任，甲方需承担申办签证费用____/人和机位订金等实际已经发生的费用。\r\n此合同签订后即为出机票的有效确认，旅行社到出票时间点直接出票，恕不另行告知。\r\n若甲方因为个人原因无法出入境或提供的护照扫描件与在机场登记护照信息不符，责任自负，乙方不承担责任。\r\n特别提醒：此线路团款中不包含甲方所在地到出境城市的往返交通费及住宿费用，如产生此部分费用，请客人自理。建议旅游者在获悉此团队已送签证后再着手预订国内段交通及住宿。');
INSERT INTO `notice_of_reservation_info` VALUES ('3', '温馨提示', '\r\n1.欧洲领馆在通常情况下，会在团队出发前1个工作日，返还团队旅游者的护照等原件给旅行社，届时才能准确获悉签证结果，若提前出签，我们将会第一时间通知您。（欧洲旅游旺季的时候，部分使领馆如意大利、希腊会出现提前半天出签的情况，此种情况较难理解，但实属欧洲使馆旺季情况下的通常做法，敬请谅解！）2.销签说明：团队回国后，需要将全团的护照原件和往返的登机牌交进领馆销签（登机牌不予退还，如您需要换取航空公司积分，可在机场航空公司柜台办理完积分之后再交给领队），也有可能领馆会要求您去面试销签，届时我们会提前通知您，请您配合；销签的时间通常为2个星期，但是旺季由于领馆繁忙，会出现一些团期销签需要二个月的时间，敬请谅解');
INSERT INTO `notice_of_reservation_info` VALUES ('4', '价格说明', '\r (1)跟团、自由行：①本起价是可选出发日期中，按双人出行共住一间房核算的最低单人价格。②产品价格会根据您所选择的出发日期、出行人数、入住酒店房型、航班或交通以及所选附加服务的不同而有所差别。\r (2)邮轮：①本起价是可选出发日期中，按每间房入住“最低可住人数”核算的最低单人价。②产品价格会根据您所选择的出发日期、出行人数、入住房型、航班或交通以及所选附加服务的不同而有所差别。\r (3)单资源：①本起价是本产品可售日期范围内的最低价。②产品价格会根据您所选择的套餐类型/资源类型、日期、数量等不同而有所差别。\r 2.参考价\r 产品展示的参考价（包括商品展示的划线价），可能是建议零售价、供应商指导价、挂牌价等，或该产品在途牛平台或门店曾经展示过的销售价；由于地区、时间的差异性和市场行情波动，建议零售价、供应商指导价、过往销售价等可能会与您购物时展示的不一致，该价格仅供您参考。');
INSERT INTO `notice_of_reservation_info` VALUES ('5', '付款方式', '面对面交易');

