  `departureTime` varchar(100) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '出发时间',
  `creationTime` date DEFAULT NULL COMMENT '下单时间',
  `phone` varchar(15) COLLATE utf8_unicode_ci DEFAULT NULL,
  `contacts` varchar(15) COLLATE utf8_unicode_ci DEFAULT NULL,
  `daoyou` varchar(10) COLLATE utf8_unicode_ci DEFAULT NULL,
