DROP TABLE IF EXISTS `scenic_spot_info`;
CREATE TABLE `scenic_spot_info` (
  `id` int(100) NOT NULL AUTO_INCREMENT COMMENT 'id',
  `name` varchar(200) COLLATE utf8_unicode_ci NOT NULL COMMENT '景点名称',
  `introduction` varchar(500) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '简介',
  `city` varchar(50) COLLATE utf8_unicode_ci DEFAULT NULL,
  `photo` varchar(100) COLLATE utf8_unicode_ci DEFAULT NULL,
  `day` varchar(20) COLLATE utf8_unicode_ci DEFAULT NULL,
  `balance` varchar(10) COLLATE utf8_unicode_ci DEFAULT '50',
  `ticket` varchar(200) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '门票',
  `type` varchar(100) COLLATE utf8_unicode_ci DEFAULT NULL,
  `remarks` varchar(2000) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '备注',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=47 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

-- ----------------------------
-- Records of scenic_spot_info
-- ----------------------------
INSERT INTO `scenic_spot_info` VALUES ('1', '<丽江-大理双飞5日游>纯玩无购物/大理海景房/丽江一晚五星酒店/360度洱海大游轮/5A玉龙雪山/崇圣寺三塔/洱海骑行/双廊/南诏风情岛', '', '云南', 'gmly2.jpg', '5', null, '2949', '1', null);
INSERT INTO `scenic_spot_info` VALUES ('23', '<泰国曼谷-芭提雅机票+当地5晚6或7日游>近万人游/美攻铁道市场/拉差达火车夜市/沙美岛出海/狂欢泼水/纯玩0自费/全程网评5星保证1晚万豪或喜来登', '', '泰国', 'a0cb0e25-6295-4e8e-99d4-5a478d85f23ctguo.jpeg', '5', null, '2983', '3', null);
INSERT INTO `scenic_spot_info` VALUES ('24', '<泰国普吉岛机票+当地5晚6日游>私家团1单1团/3晚沙滩2晚海景无边泳池房卡利马/凯悦/皮皮竹子岛出海/微风餐厅晚宴/骑大象/海豚馆VIP ', '', '泰国', 'tgg.png', '5', null, '5967', '3', null);
INSERT INTO `scenic_spot_info` VALUES ('27', '<丽江-香格里拉-泸沽湖双飞6日游>臻品8人散拼小团/真正0购物/别克GL8/奔驰商务车/玉龙雪山/探寻芳华普达措/邂逅女儿国 ', '', '云南', 'd87fab4e-16b6-4972-bcf2-e46a6bf8da82yn.jpg', '3', null, '2496', '1', null);
INSERT INTO `scenic_spot_info` VALUES ('28', '<桂林+阳朔+漓江+银子岩双动4日游>深圳往返/船游漓江/遇龙河竹筏/观千古情演出/探银子溶洞/特别安排休闲下午茶', '', '桂林', 'f5226719-d295-4686-96cc-3fc3caca6e216a4434f9-da73-4bc4-95f0-e317105ba43e.jpeg', '4', null, '863', '2', null);
INSERT INTO `scenic_spot_info` VALUES ('29', '<广州增城白水寨1日游>深圳往返，百人好评，贴心服务，户外纯玩游', '', '广州', 'e6791561-ba6d-4809-bf16-0e13bc3fddcec4e6ea1c7b7c11b106021c08cf064027_w500_h280_c1_t0.jpg', '1', null, '171', '2', null);
INSERT INTO `scenic_spot_info` VALUES ('30', '[春节]<深圳+广州2日游>纯玩无购物，白云山/黄埔军校/陈家祠/深港环岛游/中英街/地王登顶', '', '广州', '7c5e53b0-74ae-4b2f-9de5-8baad0cfd092bys.jpg', '2', null, '770', '2', null);
INSERT INTO `scenic_spot_info` VALUES ('32', '<北京5日游>牛人五星绝无暗店/自营倾情打造/热销4年/超50000人出游，故宫深度游3H含珍宝馆，获奖导游精讲漫游 ', '', '北京', 'e0b8c46e-c5c7-4806-8322-6ddab799a5dfbj.jpg', '5', null, '2513', '2', null);
INSERT INTO `scenic_spot_info` VALUES ('36', 'aiyaxiaoyanzi啊啊啊啊啊啊啊啊啊啊啊啊啊啊啊啊啊啊啊啊啊啊啊啊啊啊啊啊啊啊啊啊啊啊啊啊啊啊啊啊阿', null, '东京', '7a04afc1-73ad-4349-bb2d-3d7ab00176fb3.jpg', '1', null, '1000', '3', null);
INSERT INTO `scenic_spot_info` VALUES ('38', 'gedeshijiehenmeigedeshijiehenmeigedeshijiehenmeigedeshijiehenmei', null, '东京', 'c286d920-e85d-4f6a-b809-7f59cf8113b13.jpg', '2', null, '1000', '2', null);
INSERT INTO `scenic_spot_info` VALUES ('43', '阿萨成就感手机号采购计划苏果超市几次阿萨成就感手机号采购计划苏果超市几次阿萨成就感手机号采购计划苏果超市几次阿萨成就感手机号采购计划苏果超市几次阿萨成就感手机号采购计划苏果超市几次三番成功成功是测试更好吃色删除', null, '广州', '0d8203cb-9458-4dc1-980e-db85c57629a5logo.PNG', '1', null, '1000', '2', null);
INSERT INTO `scenic_spot_info` VALUES ('44', 'box-shadowbox-shadowbox-shadowbox-shadowbox-shadowbox-shadow', null, '东京', '50b7c997-e2eb-4643-b103-68d5df047c8dlogo.PNG', '1', null, '420466', '1', null);
INSERT INTO `scenic_spot_info` VALUES ('45', '旅游旅游旅游旅游旅游旅游旅游旅游旅游旅游旅游旅游旅游旅游', null, '广州', '4ceb8351-4d2f-4663-ad10-9eafa40e87821.jpg', '1', '50', '100', '2', null);
INSERT INTO `scenic_spot_info` VALUES ('46', '<西安秦始皇兵马俑-华清宫-骊山1日游>放心游可穿秦服体验秦风佳宴三环内接可选大唐不夜城网红打卡地散团 ', null, '西安', '5073947f-da79-4d5d-8ba7-8dbfeea15f7ctgg5.jpeg', '2', '50', '2000', '2', null);

