  `id` int(100) NOT NULL AUTO_INCREMENT,
  `userid` varchar(100) COLLATE utf8_unicode_ci DEFAULT NULL,
  `cover` varchar(2000) COLLATE utf8_unicode_ci DEFAULT NULL,
  `title` varchar(100) COLLATE utf8_unicode_ci DEFAULT NULL,
  `introduction` varchar(2000) COLLATE utf8_unicode_ci DEFAULT NULL,
  `text` varchar(15000) COLLATE utf8_unicode_ci DEFAULT NULL,
  `fabulous` varchar(200) COLLATE utf8_unicode_ci DEFAULT '100',
  `commentid` varchar(200) COLLATE utf8_unicode_ci DEFAULT NULL,
  `creationtime` date DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=13 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

